package com.practicaljava.lesson20;public class TestThreadsWait {    public static void main(String args[]){        Object theLockKeeper = new Object();        MarketNews3 mn = new MarketNews3(theLockKeeper);         mn.start();         Portfolio3 p = new Portfolio3("Portfolio data");         p.start();        synchronized (theLockKeeper) {            try{                System.out.println("\tStarting to waitâ€¦");                theLockKeeper.wait(15000);            } catch (InterruptedException e){                System.out.println("\tThe main thread is interrupted");            }        }         System.out.println( "\tThe main method of TestThreads3 is finished");    }}