package com.practicaljava.lesson21;public class TestThreadJoin {    public static void main(String args[]){                com.practicaljava.lesson21.MarketNews3 mn = new com.practicaljava.lesson21.MarketNews3("Market News");         mn.start();         com.practicaljava.lesson21.Portfolio3 p = new com.practicaljava.lesson21.Portfolio3("Portfolio data");         p.start();                  try{        	 mn.join();        	 p.join();         }catch (InterruptedException e){        	 e.printStackTrace();         }                  System.out.println( "The main method of TestThreadJoin is finished");    }}